import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.div`
  padding: 30px;
  background-color: #f0f4f8;
  border-radius: 12px;
  max-width: 500px;
  margin: auto;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 10px;
  height: 80px;
  margin: 10px 0;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const Button = styled.button`
  background-color: #3498db;
  color: white;
  padding: 12px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  width: 100%;
`;

const HotelForm = () => {
  const [formData, setFormData] = useState({
    nom: '',
    description: '',
    prix: '',
    disponible: true,
    adresse: '',
    image: '',
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/hotels', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      alert("✅ Hôtel enregistré avec succès !");
      console.log(data);
    } catch (err) {
      console.error('❌ Erreur :', err);
    }
  };

  return (
    <FormContainer>
      <h2>Ajouter un Hôtel</h2>
      <form onSubmit={handleSubmit}>
        <Input type="text" name="nom" placeholder="Nom" onChange={handleChange} required />
        <TextArea name="description" placeholder="Description" onChange={handleChange} required />
        <Input type="number" name="prix" placeholder="Prix" onChange={handleChange} required />
        <Input type="text" name="adresse" placeholder="Adresse" onChange={handleChange} />
        <Input type="text" name="image" placeholder="URL de la photo" onChange={handleChange} required />
        <Button type="submit">Enregistrer un hôtel</Button>
      </form>
    </FormContainer>
  );
};

export default HotelForm;
