import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const FormContainer = styled.div`
  padding: 20px;
`;

const Input = styled.input`
  margin: 10px;
  padding: 10px;
  width: 80%;
`;

const Button = styled.button`
  padding: 10px 20px;
  margin-top: 10px;
  background-color: #2c3e50;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
`;

function HotelForm() {
  const [formData, setFormData] = useState({
    nom: '',
    description: '',
    prix: '',
    photo: '',
    disponibilite: true,
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'prix' ? Number(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/hotels', formData);
      alert('Hôtel enregistré avec succès !');
      setFormData({
        nom: '',
        description: '',
        prix: '',
        photo: '',
        disponibilite: true
      });
    } catch (err) {
      console.error(err);
      alert('Erreur lors de l’enregistrement');
    }
  };

  return (
    <FormContainer>
      <h2>Ajouter un Hôtel</h2>
      <form onSubmit={handleSubmit}>
        <Input name="nom" placeholder="Nom" value={formData.nom} onChange={handleChange} required />
        <Input name="description" placeholder="Description" value={formData.description} onChange={handleChange} required />
        <Input name="prix" placeholder="Prix" value={formData.prix} onChange={handleChange} required />
        <Input name="image" placeholder="URL de la photo" value={formData.image} onChange={handleChange} required />
        <Button type="submit">Enregistrer un hôtel</Button>
      </form>
    </FormContainer>
  );
}

export default HotelForm;
