  color: #2c3e50;
`;

const Button = styled.button`
  background-color: #3498db;
  color: white;
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
`;

function App() {
  return (
    <div>
      <HotelForm />
    </div>
  );
}

export default App;
