import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Container = styled.div`
  text-align: center;
  padding: 50px;
`;

const Title = styled.h1`
  color: #2c3e50;
  margin-bottom: 30px;
`;

const NavButton = styled(Link)`
  display: inline-block;
  background-color: #2980b9;
  color: white;
  text-decoration: none;
  padding: 15px 30px;
  border-radius: 10px;
  margin: 10px;
  font-size: 18px;
`;

function ListeHotel() {
  return (
    <Container>
      <Title>Tableau de bord - Réservation Hôtel</Title>
      <NavButton to="/ajouter">Enregistrer un hôtel</NavButton>
      <NavButton to="/liste">Liste des hôtels</NavButton>
    </Container>
  );
}

export default ListeHotel;
