// src/components/ListeHotel.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 30px;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  margin: 0 auto 30px;
  display: block;
  border: 1px solid #ccc;
  border-radius: 8px;
  font-size: 16px;
`;

const HotelGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
`;

const HotelCard = styled.div`
  background: white;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 0 12px rgba(0,0,0,0.1);
  text-align: center;
`;

const HotelImage = styled.img`
  width: 100%;
  height: 180px;
  object-fit: cover;
  border-radius: 8px;
`;

function ListeHotel() {
  const [hotels, setHotels] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/hotels')
      .then(response => setHotels(response.data))
      .catch(error => console.error("Erreur lors de la récupération des hôtels :", error));
  }, []);

  const filteredHotels = hotels.filter(hotel =>
    hotel.nom.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container>
      <h2 style={{ textAlign: 'center' }}>Liste des Hôtels</h2>
      <SearchInput
        type="text"
        placeholder="Recherche"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <HotelGrid>
        {filteredHotels.map(hotel => (
          <HotelCard key={hotel._id}>
            <HotelImage src={hotel.image || hotel.photo} alt={hotel.nom} />
            <p style={{ color: 'brown' }}>{hotel.description}</p>
            <h3>{hotel.nom}</h3>
            <p>{hotel.prix} FCFA</p>
          </HotelCard>
        ))}
      </HotelGrid>
    </Container>
  );
}

export default ListeHotel;
