import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 30px;
`;

const SearchBarContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const CreateButton = styled.button`
  padding: 10px 20px;
  background-color: #27ae60;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
`;

const HotelGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
`;

const HotelCard = styled.div`
  background: white;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 0 12px rgba(0,0,0,0.1);
  text-align: center;
`;

const HotelImage = styled.img`
  width: 100%;
  height: 180px;
  object-fit: cover;
  border-radius: 8px;
`;

function ListeHotel() {
  const [hotels, setHotels] = useState([]);
  const [search, setSearch] = useState('');
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/api/hotels')
      .then(response => setHotels(response.data))
      .catch(error => console.error("Erreur lors de la récupération des hôtels :", error));
  }, []);

  const filteredHotels = hotels.filter(hotel =>
    hotel.nom.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container>
      <SearchBarContainer>
        <SearchInput
          type="text"
          placeholder="🔍 Rechercher un hôtel..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <CreateButton onClick={() => window.location.href = '/'}>Créer un nouveau hôtel</CreateButton>
      </SearchBarContainer>

      <HotelGrid>
        {filteredHotels.map(hotel => (
          <HotelCard key={hotel._id}>
            <HotelImage src={hotel.image} alt={hotel.nom} />
            <p style={{ color: 'brown' }}>{hotel.description}</p>
            <h3>{hotel.nom}</h3>
            <p>{hotel.prix} XOF par nuit</p>
          </HotelCard>
        ))}
      </HotelGrid>
    </Container>
  );
}

export default ListeHotel;
