import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HotelForm from './components/HotelForm';
import ListeHotel from './components/ListeHotel';
// plus tard : import ListeDesHotels from './components/ListeDesHotels';

const Container = styled.div`
  text-align: center;
  padding: 50px;
  background-color: #f2f2f2;
`;

const Title = styled.h1`
  color: #2c3e50;
`;

const Button = styled.button`
  background-color: #3498db;
  color: white;
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
`;

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<ListeHotel />} />
        <Route path="/ajouter" element={<HotelForm />} />
        {/* <Route path="/liste" element={<ListeDesHotels />} /> */}
      </Routes>
    </Router>
  );
}

export default App;
