import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const FormContainer = styled.div`
  max-width: 400px;
  margin: auto;
  padding: 20px;
  background-color: #ffffff;
  border-radius: 12px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Input = styled.input`
  display: block;
  width: 100%;
  padding: 12px;
  margin-bottom: 16px;
  border: 1px solid #ccc;
  border-radius: 8px;
`;

const Button = styled.button`
  background-color: #3498db;
  color: white;
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  width: 100%;
`;

function HotelForm() {
  const [formData, setFormData] = useState({
    nom: '',
    description: '',
    prix: '',
    photo: '',
    disponibilite: true,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/hotels', formData);
      alert('Hôtel enregistré avec succès !');
      setFormData({
        nom: '',
        description: '',
        prix: '',
        photo: '',
        disponibilite: true,
      });
    } catch (error) {
      console.error('Erreur lors de l’enregistrement', error);
      alert("Échec de l'enregistrement");
    }
  };

  return (
    <FormContainer>
      <h2>Ajouter un Hôtel</h2>
      <form onSubmit={handleSubmit}>
        <Input name="nom" placeholder="Nom" value={formData.nom} onChange={handleChange} required/>
        <Input name="description" placeholder="Description" value={formData.description} onChange={handleChange} required/>
        <Input name="prix" placeholder="Prix" type="number" value={formData.prix} onChange={handleChange} required/>
        <Input name="image" placeholder="URL de la " value={formData.photo} onChange={handleChange} required/>
        <label>
          <input type="checkbox" name="disponibilite" checked={formData.disponibilite} onChange={handleChange}/>
          Disponible
        </label>
        <Button type="submit">Enregistrer un hôtel</Button>
      </form>
    </FormContainer>
  );
}

export default HotelForm;
