// src/components/ListeHotel.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const HotelGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  padding: 30px;
`;

const HotelCard = styled.div`
  background: white;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 0 12px rgba(0,0,0,0.1);
  text-align: center;
`;

const HotelImage = styled.img`
  width: 100%;
  height: 180px;
  object-fit: cover;
  border-radius: 8px;
`;

function ListeHotel() {
  const [hotels, setHotels] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/hotels')
      .then(response => setHotels(response.data))
      .catch(error => console.error("Erreur lors de la récupération des hôtels :", error));
  }, []);

  return (
    <div>
      <h2 style={{ textAlign: 'center', marginTop: '20px' }}>Liste des Hôtels</h2>
      <HotelGrid>
        {hotels.map(hotel => (
          <HotelCard key={hotel._id}>
            <HotelImage src={hotel.image} alt={hotel.nom} />
            <p style={{ color: 'brown' }}>{hotel.description}</p>
            <h3>{hotel.nom}</h3>
            <p>{hotel.prix} FCFA</p>
          </HotelCard>
        ))}
      </HotelGrid>
    </div>
  );
}

export default ListeHotel;
